import React, { useEffect, useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Dashboard = () => {
  const { user, isAuthenticated, logout } = useAuth();
  const navigate = useNavigate();
  const [profile, setProfile] = useState(null);

  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    // Since we don't have a dedicated /me endpoint documented yet, we'll use the user from context
    // or simulate a fetch. In a real app, we'd fetch user data here.
    if (user) {
      setProfile(user);
    }
  }, [isAuthenticated, navigate, user]);

  if (!isAuthenticated) return null;

  return (
    <div className="content-wrapper" style={{ padding: '40px 80px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '40px' }}>
        <h1>Dashboard</h1>
        <button 
          onClick={logout}
          style={{ padding: '8px 16px', background: 'transparent', border: '1px solid #333', color: '#fff', borderRadius: '4px', cursor: 'pointer' }}
        >
          Logout
        </button>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '20px' }}>
        <div className="card">
          <h3>Welcome, {profile?.fullName || 'User'}</h3>
          <p>Role: <span style={{ textTransform: 'capitalize', color: '#00ff88' }}>{profile?.role || 'User'}</span></p>
          <p>Email: {profile?.email}</p>
        </div>

        <div className="card">
          <h3>Market Status</h3>
          <p style={{ fontSize: '24px', fontWeight: 'bold', color: '#00ff88' }}>ACTIVE</p>
          <p>System operational. Data stream live.</p>
        </div>

        <div className="card">
          <h3>Your Subscriptions</h3>
          <p>Plan: Basic Access</p>
          <p>Status: Active</p>
        </div>
      </div>

      <div className="card" style={{ marginTop: '20px' }}>
        <h3>Real-time Analytics</h3>
        <div style={{ height: '300px', background: '#111', borderRadius: '8px', display: 'flex', alignItems: 'center', justifyContent: 'center', border: '1px dashed #333' }}>
          <p style={{ color: '#666' }}>Interactive Chart Component Placeholder</p>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
